import React from 'react';
import { Link } from 'react-router-dom';

function Overlay({ onClose, isOpen, handleToggle }) {
  return (
    <div
      className="overlay"
      style={{ display: isOpen ? 'block' : 'none' }}
      onClick={onClose}
    >
      <nav className="overlay__nav" onClick={(e) => e.stopPropagation()}>
        <div
          className={`hamburger ${isOpen ? "change" : ""}`}
          onClick={handleToggle}
        >
          <div className="bar1"></div>
          <div className="bar2"></div>
          <div className="bar3"></div>
        </div>
        <ul className='overlay__menu'>
          <li className="nav__link mobile__nav--link" onClick={onClose}>
            <Link to="/">Home</Link>
          </li>
          <li className="nav__link mobile__nav--link" onClick={onClose}>
            <Link to="/search">Find Events</Link>
          </li>
          <li className="nav__link mobile__nav--link" onClick={onClose}>
            <Link to="/contact">Contact</Link>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Overlay;
